    "# READING THE DATA \n",
    "\n",
    "## I started by loading necessary packages \n",
    "\n",
    "library(\"tidyverse\")\n",
    "library(\"lubridate\")\n",
    "library(\"skimr\")\n",
    "\n",
    "## So, I loaded the datasets from file directory.\n",
    "\n",
    "dailyActivity_merged <- read_csv(\"/kaggle/input/fitbit/Fitabase Data 4.12.16-5.12.16/dailyActivity_merged.csv\")\n",
    "#View(dailyActivity_merged)\n",
    "#head(dailyActivity_merged)\n",
    "hourlyCalories_merged <- read_csv(\"/kaggle/input/fitbit/Fitabase Data 4.12.16-5.12.16/hourlyCalories_merged.csv\")\n",
    "# head(hourlyCalories_merged)\n",
    "sleepDay_merged <- read_csv(\"/kaggle/input/fitbit/Fitabase Data 4.12.16-5.12.16/sleepDay_merged.csv\")\n",
    "# View(sleepDay_merged)\n",
    " head(sleepDay_merged)\n",
    "hourlySteps_merged <- read_csv(\"/kaggle/input/fitbit/Fitabase Data 4.12.16-5.12.16/hourlySteps_merged.csv\")\n",
